    private void btnComenzarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnComenzarActionPerformed
        int eventos =Integer.parseInt(cboEventos.getSelectedItem().toString());
        double cantidadd = Double.parseDouble(txtCantidadd.getText().toString());
        double result1 = 0, i = 1;
        
        double Dialogcantidad[] = new double [1000];
        
        for(int j=1; j<=eventos; j++)
        {
            JTextField  cantidad1 = new JTextField();

            Object[] mensaje =  {
                "Cantidad: ", cantidad1
            };
            int Opcion = JOptionPane.showConfirmDialog(null, mensaje,"Ingerese Cantidad: ", JOptionPane.OK_CANCEL_OPTION, JOptionPane.INFORMATION_MESSAGE);
            if(Opcion==JOptionPane.OK_OPTION) // si el usiario presiona aceptar
            {
                Dialogcantidad[j] = Integer.parseInt(cantidad1.getText());
            }
        }
        
        //ComparaciÃ³n a puro WHILE
        while (result1 != cantidadd)
        {
            result1 = 0;
            double tasa = i/10000;
            for(int j=1; j<=eventos; j++)
                {
                    result1 += Dialogcantidad[j] / (Math.pow((1+tasa), j));
                }
            i++;
            if ((int)result1 == cantidadd)
            {
                JOptionPane.showMessageDialog(null, "TIR es: " + tasa);
                return;
            }
        }
        
    }//GEN-LAST:event_btnComenzarActionPerformed

